mapper.select:
SELECT 
	`id`, 
	`symbol`, 
	`currency_id1`, 
	`currency_id2`, 
	`price_decimal_digits`, 
	`count_decimal_digits`, 
	`min_price`, 
	`min_count`, 
	`min_amount`, 
	`max_price`, 
	`max_count`, 
	`max_amount`, 
	`status`, 
	`sort`, 
	`create_time`, 
	`update_time`
FROM t_currency_trade

	
	AND `symbol` = #{symbol,jdbcType=VARCHAR}
	
	
	AND `currency_id1` = #{currencyId1,jdbcType=INTEGER}
	
	
	AND `currency_id2` = #{currencyId2,jdbcType=INTEGER}
	
	
	AND `price_decimal_digits` = #{priceDecimalDigits,jdbcType=INTEGER}
	
	
	AND `count_decimal_digits` = #{countDecimalDigits,jdbcType=INTEGER}
	
	
	AND `min_price` = #{minPrice,jdbcType=DECIMAL}
	
	
	AND `min_count` = #{minCount,jdbcType=DECIMAL}
	
	
	AND `min_amount` = #{minAmount,jdbcType=DECIMAL}
	
	
	AND `max_price` = #{maxPrice,jdbcType=DECIMAL}
	
	
	AND `max_count` = #{maxCount,jdbcType=DECIMAL}
	
	
	AND `max_amount` = #{maxAmount,jdbcType=DECIMAL}
	
	
	AND `status` = #{status,jdbcType=INTEGER}
	
	
	AND `sort` = #{sort,jdbcType=INTEGER}
	
	
	AND `create_time` = #{createTime,jdbcType=TIMESTAMP}
	
	
	AND `update_time` = #{updateTime,jdbcType=TIMESTAMP}
	

LIMIT 1

