mapper.select:
SELECT 
	`id`, 
	`user_id`, 
	`login_name`, 
	`lgoin_type`, 
	`login_ip`, 
	`create_time`, 
	`update_time`, 
	`status`, 
	`extend1`, 
	`extend2`, 
	`exten3`
FROM t_login_log

	
	AND `user_id` = #{userId,jdbcType=BIGINT}
	
	
	AND `login_name` = #{loginName,jdbcType=BIGINT}
	
	
	AND `lgoin_type` = #{lgoinType,jdbcType=INTEGER}
	
	
	AND `login_ip` = #{loginIp,jdbcType=VARCHAR}
	
	
	AND `create_time` = #{createTime,jdbcType=TIMESTAMP}
	
	
	AND `update_time` = #{updateTime,jdbcType=TIMESTAMP}
	
	
	AND `status` = #{status,jdbcType=INTEGER}
	
	
	AND `extend1` = #{extend1,jdbcType=VARCHAR}
	
	
	AND `extend2` = #{extend2,jdbcType=VARCHAR}
	
	
	AND `exten3` = #{exten3,jdbcType=VARCHAR}
	

LIMIT 1

