	AND `coin_type` = #{wConfigWallet.coinType,jdbcType=VARCHAR}
	
	
	AND `wallet_type` = #{wConfigWallet.walletType,jdbcType=VARCHAR}
	
	
	AND `address` = #{wConfigWallet.address,jdbcType=VARCHAR}
	
	
