mapper.select:
SELECT 
	`dict_code`, 
	`dict_sort`, 
	`dict_label`, 
	`dict_value`, 
	`dict_type`, 
	`css_class`, 
	`list_class`, 
	`is_default`, 
	`status`, 
	`create_by`, 
	`create_time`, 
	`update_by`, 
	`update_time`, 
	`remark`
FROM sys_dict_data

	
	AND `dict_sort` = #{dictSort,jdbcType=INTEGER}
	
	
	AND `dict_label` = #{dictLabel,jdbcType=VARCHAR}
	
	
	AND `dict_value` = #{dictValue,jdbcType=VARCHAR}
	
	
	AND `dict_type` = #{dictType,jdbcType=VARCHAR}
	
	
	AND `css_class` = #{cssClass,jdbcType=VARCHAR}
	
	
	AND `list_class` = #{listClass,jdbcType=VARCHAR}
	
	
	AND `is_default` = #{isDefault,jdbcType=CHAR}
	
	
	AND `status` = #{status,jdbcType=CHAR}
	
	
	AND `create_by` = #{createBy,jdbcType=VARCHAR}
	
	
	AND `create_time` = #{createTime,jdbcType=TIMESTAMP}
	
	
	AND `update_by` = #{updateBy,jdbcType=VARCHAR}
	
	
	AND `update_time` = #{updateTime,jdbcType=TIMESTAMP}
	
	
	AND `remark` = #{remark,jdbcType=VARCHAR}
	

LIMIT 1

