mapper.select:
SELECT 
	`info_id`, 
	`login_name`, 
	`ipaddr`, 
	`login_location`, 
	`browser`, 
	`os`, 
	`status`, 
	`msg`, 
	`login_time`
FROM sys_logininfor

	
	AND `login_name` = #{loginName,jdbcType=VARCHAR}
	
	
	AND `ipaddr` = #{ipaddr,jdbcType=VARCHAR}
	
	
	AND `login_location` = #{loginLocation,jdbcType=VARCHAR}
	
	
	AND `browser` = #{browser,jdbcType=VARCHAR}
	
	
	AND `os` = #{os,jdbcType=VARCHAR}
	
	
	AND `status` = #{status,jdbcType=CHAR}
	
	
	AND `msg` = #{msg,jdbcType=VARCHAR}
	
	
	AND `login_time` = #{loginTime,jdbcType=TIMESTAMP}
	

LIMIT 1

