mapper.select:
SELECT 
	`oper_id`, 
	`title`, 
	`business_type`, 
	`method`, 
	`operator_type`, 
	`oper_name`, 
	`dept_name`, 
	`oper_url`, 
	`oper_ip`, 
	`oper_location`, 
	`oper_param`, 
	`status`, 
	`error_msg`, 
	`oper_time`
FROM sys_oper_log

	
	AND `title` = #{title,jdbcType=VARCHAR}
	
	
	AND `business_type` = #{businessType,jdbcType=INTEGER}
	
	
	AND `method` = #{method,jdbcType=VARCHAR}
	
	
	AND `operator_type` = #{operatorType,jdbcType=INTEGER}
	
	
	AND `oper_name` = #{operName,jdbcType=VARCHAR}
	
	
	AND `dept_name` = #{deptName,jdbcType=VARCHAR}
	
	
	AND `oper_url` = #{operUrl,jdbcType=VARCHAR}
	
	
	AND `oper_ip` = #{operIp,jdbcType=VARCHAR}
	
	
