mapper.select:
SELECT 
	`sessionId`, 
	`login_name`, 
	`dept_name`, 
	`ipaddr`, 
	`login_location`, 
	`browser`, 
	`os`, 
	`status`, 
	`start_timestamp`, 
	`last_access_time`, 
	`expire_time`
FROM sys_user_online

	
	AND `login_name` = #{loginName,jdbcType=VARCHAR}
	
	
	AND `dept_name` = #{deptName,jdbcType=VARCHAR}
	
	
	AND `ipaddr` = #{ipaddr,jdbcType=VARCHAR}
	
	
	AND `login_location` = #{loginLocation,jdbcType=VARCHAR}
	
	
	AND `browser` = #{browser,jdbcType=VARCHAR}
	
	
	AND `os` = #{os,jdbcType=VARCHAR}
	
	
	AND `status` = #{status,jdbcType=VARCHAR}
	
	
	AND `start_timestamp` = #{startTimestamp,jdbcType=TIMESTAMP}
	
	
	AND `last_access_time` = #{lastAccessTime,jdbcType=TIMESTAMP}
	
	
	AND `expire_time` = #{expireTime,jdbcType=INTEGER}
	

LIMIT 1

