SELECT 
	`id`, 
	`symbol`, 
	`currency_id1`, 
	`currency_id2`, 
	`price_decimal_digits`, 
	`count_decimal_digits`, 
	`min_buy_count`, 
	`min_Buy_price`, 
	`min_buy_amount`, 
	`max_buy_count`, 
	`max_buy_amount`, 
	`max_buy_price`, 
	`status`, 
	`sort`, 
	`create_time`, 
	`update_time`
FROM t_currency_trade


	AND `symbol` = #{symbol,jdbcType=VARCHAR}


	AND `currency_id1` = #{currencyId1,jdbcType=INTEGER}


	AND `currency_id2` = #{currencyId2,jdbcType=INTEGER}


	AND `price_decimal_digits` = #{priceDecimalDigits,jdbcType=INTEGER}


	AND `count_decimal_digits` = #{countDecimalDigits,jdbcType=INTEGER}


	AND `min_buy_count` = #{minBuyCount,jdbcType=DECIMAL}


	AND `min_Buy_price` = #{minBuyPrice,jdbcType=DECIMAL}


	AND `min_buy_amount` = #{minBuyAmount,jdbcType=DECIMAL}


	AND `max_buy_count` = #{maxBuyCount,jdbcType=DECIMAL}


	AND `max_buy_amount` = #{maxBuyAmount,jdbcType=DECIMAL}


	AND `max_buy_price` = #{maxBuyPrice,jdbcType=DECIMAL}


	AND `status` = #{status,jdbcType=INTEGER}


	AND `sort` = #{sort,jdbcType=INTEGER}


	AND `create_time` = #{createTime,jdbcType=TIMESTAMP}


	AND `update_time` = #{updateTime,jdbcType=TIMESTAMP}

