	AND `currency_id1` = #{tCurrencyTrade.currencyId1,jdbcType=INTEGER}


	AND `currency_id2` = #{tCurrencyTrade.currencyId2,jdbcType=INTEGER}


	AND `price_decimal_digits` = #{tCurrencyTrade.priceDecimalDigits,jdbcType=INTEGER}


	AND `count_decimal_digits` = #{tCurrencyTrade.countDecimalDigits,jdbcType=INTEGER}


