mapper.select:
SELECT 
	`id`, 
	`symbol`, 
	`low`, 
	`high`, 
	`open`, 
	`close`, 
	`volume`, 
	`granularity`, 
	`time`, 
	`create_time`, 
	`update_time`
FROM t_kline

	
	AND `symbol` = #{symbol,jdbcType=VARCHAR}
	
	
	AND `low` = #{low,jdbcType=DECIMAL}
	
	
	AND `high` = #{high,jdbcType=DECIMAL}
	
	
	AND `open` = #{open,jdbcType=DECIMAL}
	
	
	AND `close` = #{close,jdbcType=DECIMAL}
	
	
	AND `volume` = #{volume,jdbcType=DECIMAL}
	
	
	AND `granularity` = #{granularity,jdbcType=VARCHAR}
	
	
	AND `time` = #{time,jdbcType=TIMESTAMP}
	
	
	AND `create_time` = #{createTime,jdbcType=TIMESTAMP}
	
	
	AND `update_time` = #{updateTime,jdbcType=TIMESTAMP}
	

LIMIT 1

