mapper.select:
SELECT
ca.name,
ca.phone,

FROM   g_beta_locked_account  la LEFT JOIN
c_personal_card  ca ON  la.user_id = ca.user_id


        AND DATE_FORMAT(la.create_time, '%Y-%m-%d') >= DATE_FORMAT(#{dto.params.beginTime},'%Y-%m-%d')


        AND DATE_FORMAT(la.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{dto.params.endTime}, '%Y-%m-%d')


        AND CONCAT(
        ca.name,
        ca.phone
        ) like concat('%',#{dto.name}, '%')


        AND la.base_balance >=#{dto.startBaseBanlence}


        AND la.base_balance <=#{dto.endBaseBanlence}


        AND la.balance >=#{dto.startBanlence}


        AND la.balance <=#{dto.endBanlence}

SELECT ca.name, ca.phone, la.id, la.create_time AS createTime, la.update_time AS updateTime, la.user_id AS userId, round(la.base_balance,2) AS baseBalance, round(la.balance,2) AS balance, round(la.total_enter,2) AS totalEnter, round(la.total_exit,2) AS totalExit, la.version FROM g_beta_locked_account la LEFT JOIN c_personal_card ca ON la.user_id = ca.user_id
AND DATE_FORMAT(la.create_time, '%Y-%m-%d') >= DATE_FORMAT(#{dto.params.beginTime},'%Y-%m-%d')
AND DATE_FORMAT(la.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{dto.params.endTime}, '%Y-%m-%d')
AND CONCAT( ca.name, ca.phone ) like concat('%',#{dto.name}, '%')
AND la.base_balance >=#{dto.startBaseBanlence}
AND la.base_balance <=#{dto.endBaseBanlence}
AND la.balance >=#{dto.startBanlence}
AND la.balance <=#{dto.endBanlence}
order by la.create_time desc
