SELECT 
	`id`, 
	`user_id`, 
	`currency_trade_id`, 
	`price`, 
	`amount`, 
	`success_amount`, 
	`count`, 
	`left_count`, 
	`fees`, 
	`left_fees`, 
	`status`, 
	`entrust_type`, 
	`entrust_direction`, 
	`version`, 
	`create_time`, 
	`update_time`
FROM t_entrust

	
	AND `user_id` = #{userId,jdbcType=BIGINT}
	
	
	AND `currency_trade_id` = #{currencyTradeId,jdbcType=INTEGER}
	
	
	AND `price` = #{price,jdbcType=DECIMAL}
	
	
	AND `amount` = #{amount,jdbcType=DECIMAL}
	
	
	AND `success_amount` = #{successAmount,jdbcType=DECIMAL}
	
	
	AND `count` = #{count,jdbcType=DECIMAL}
	
	
	AND `left_count` = #{leftCount,jdbcType=DECIMAL}
	
	
	AND `fees` = #{fees,jdbcType=DECIMAL}
	
	
	AND `left_fees` = #{leftFees,jdbcType=DECIMAL}
	
	
	AND `status` = #{status,jdbcType=INTEGER}
	
	
	AND `entrust_type` = #{entrustType,jdbcType=INTEGER}
	
	
	AND `entrust_direction` = #{entrustDirection,jdbcType=INTEGER}
	
	
	AND `version` = #{version,jdbcType=INTEGER}
	
	
	AND `create_time` = #{createTime,jdbcType=TIMESTAMP}
	
	
	AND `update_time` = #{updateTime,jdbcType=TIMESTAMP}

