mapper.select:
SELECT 
	`id`, 
	`user_id`, 
	`currency`, 
	`count`, 
	`business_number`, 
	`status`, 
	`typeChannel`, 
	`sourceChannel`, 
	`desc`, 
	`create_time`, 
	`update_time`
FROM t_wallet_transfer


	AND `user_id` = #{userId,jdbcType=BIGINT}


	AND `currency` = #{currency,jdbcType=INTEGER}


	AND `count` = #{count,jdbcType=DECIMAL}


	AND `business_number` = #{businessNumber,jdbcType=VARCHAR}


	AND `status` = #{status,jdbcType=INTEGER}


	AND `typeChannel` = #{typeChannel,jdbcType=INTEGER}


	AND `sourceChannel` = #{sourceChannel,jdbcType=INTEGER}


	AND `desc` = #{desc,jdbcType=VARCHAR}


	AND `create_time` = #{createTime,jdbcType=TIMESTAMP}


	AND `update_time` = #{updateTime,jdbcType=TIMESTAMP}

SELECT COUNT(1)
FROM t_wallet_transfer


	AND `user_id` = #{tWalletTransfer.userId,jdbcType=BIGINT}


	AND `currency` = #{tWalletTransfer.currency,jdbcType=INTEGER}


	AND `count` = #{tWalletTransfer.count,jdbcType=DECIMAL}


	AND `business_number` = #{tWalletTransfer.businessNumber,jdbcType=VARCHAR}


	AND `status` = #{tWalletTransfer.status,jdbcType=INTEGER}


	AND `typeChannel` = #{tWalletTransfer.typeChannel,jdbcType=INTEGER}


	AND `sourceChannel` = #{tWalletTransfer.sourceChannel,jdbcType=INTEGER}


	AND `desc` = #{tWalletTransfer.desc,jdbcType=VARCHAR}


	AND `create_time` = #{tWalletTransfer.createTime,jdbcType=TIMESTAMP}


	AND `update_time` = #{tWalletTransfer.updateTime,jdbcType=TIMESTAMP}

