mapper.select:
SELECT r.currency,r.create_time AS time,r.after_amount AS afterAmount,r.before_amount AS beforeAmount,r.change_amount + IFNULL(o.fee,0) AS changeAmount,IFNULL(o.fee,0) AS fee, CASE WHEN r.type = 1 THEN '充币' WHEN r.type = 2 THEN '提币' WHEN r.type = 3 THEN '轉入資金賬戶' WHEN r.type = 4 THEN '轉出幣幣賬戶' WHEN r.type = 5 THEN '轉出法幣賬戶' WHEN r.type = 6 THEN '轉入資金賬戶' ELSE '' END AS type FROM t_wallet_fund_account_record r FROM w_wallet_account_record r LEFT JOIN w_order o ON o.order_id = r.order_id LEFT JOIN t_currency c ON c.id = r.currency_id WHERE r.user_id = #{userId}
AND r.currency = #{currency}
AND r.type in
#{item}
ORDER BY r.id DESC
