mapper.update:
UPDATE c_personal_account
balance = #{balance}, total_enter = #{totalEnter}, update_time = now(), version = version + 1
WHERE id = #{id} AND version = #{version}

UPDATE c_personal_account
balance = #{balance}, total_exit = #{totalExit}, update_time = now(), version = version + 1
WHERE id = #{id} AND version = #{version}

        UPDATE c_personal_account
		SET
			used_margin = used_margin - #{num},
			version = version + 1,
			total_exit = total_exit + #{num}
		WHERE id = #{id} AND version = #{version}
